class AnimatedSpriteManager extends PIXI.extras.AnimatedSprite{constructor(textures,autoUpdate){super(textures,autoUpdate);this._totalTextures=textures;} playAnimatedState(frames){this.stop();if(Array.isArray(frames)) {let textures=[];frames.forEach(texture=>{textures.push(this._totalTextures[texture]);});this.textures=textures;this.gotoAndPlay(0);}else {this.textures=[this._totalTextures[frames]];this.gotoAndStop(0);}}} PIXI.AnimatedSpriteManager=AnimatedSpriteManager;
